import React from 'react'
import styled from 'styled-components'
import Family from '../../images/family.png' 
import Home from '../../images/home.png' 
import useForm from '../../utils/useForm'

export default function FormOne({submitForm}) {
    const {handleChange, values} = useForm(submitForm);
    console.log(values.option)
    return (
    <Card>
        <TopSection>
            <h2>
                Protect your family today...
            </h2>
        </TopSection>
        <Title>
            What would you like to cover?
        </Title>
        <ButtonWrap >
                <Button >
                  <input type="radio" name="option" id="option1" autocomplete="new" checked value={values.option}  onSelect={handleChange}/>
                  <img src={Family} />                  
                  <p>My Family</p>
                </Button>
                <Button >
                  <input type="radio" name="option" id="option2" autocomplete="off" checked value="Family & Mortgage" onSelect={handleChange}/>
                  <img src={Home} />
                  <p> Family & Mortgage</p>
                </Button>
        </ButtonWrap>
              <CardBtnWrap>
                <CardBtn>  GET MY QUOTE</CardBtn>
                {/* <button className="btn btn-outline-primary btn-lg">  Next  </button> */}
              </CardBtnWrap>

    </Card>
    )
}
const Card = styled.div`
display:flex;
flex-direction:column;
height:100%;
width:100%;
[type=radio] {
 margin:0;padding:0;
 -webkit-appearance:none;
    -moz-appearance:none;
         appearance:none;
}


`
const TopSection = styled.div`
background:#193559;
border-radius:10px 10px 0 0;
width:100%;
height:100px;
h2{
    padding:2.6rem 0;
    text-align:center;
    font-size: 2rem;
    color:#fff
}
`
const Title = styled.div`
text-align:center;
margin:1.5rem 0;
font-size:18px;
font-weight:700;
`
const ButtonWrap = styled.div`
display:flex;
justify-content:center;
flex-direction:row;
column-gap:1.8rem;
margin:0 4rem;
@media screen and (max-width:768px){
    flex-direction:column;
    row-gap:2rem;
    align-items:center
}
`
const Button = styled.label`
cursor: pointer;
white-space: nowrap;
border:solid thin #37bfed;
outline:none;
background:none;
border-radius:15px;
width:400px;
height:200px;
display:flex;
flex-direction:column;
justify-content:center;
align-items:center;
img{
    width:110px;
    height:110px
}
@media screen and (max-width:768px){
    display:flex;
    flex-direction:row;
    justify-content:flex-start;
    align-items:center;
    width:350px;
    height:80px;

    img{
        width:50px;
        height:50px;
        margin:0 2rem;
    }

}
@media screen and (max-width:400px){
    display:flex;
    justify-content:flex-start;
    align-items:center;
    width:200px;
    height:80px;

    img{
        width:50px;
        height:50px;
        margin:0 2rem;
    }

}
&:hover{
    transform: scale(1.04);
    transition: 0.3s ease-in-out;
    background:#def3fa;
}
`
const CardBtnWrap = styled.div`
display:flex;
justify-content:center;
align-items:center;
margin:3rem 0 2rem 0;
z-index:10;


`
const CardBtn = styled.button`
background:linear-gradient(#84CA1E,#66AC00 50%);
padding:1.7rem 8rem;
border:none;
outline:none;
cursor: pointer;
border-radius:6px;
color:#fff;
font-weight:700;
font-size:1.4rem;
&:hover{
    transform:scale(1.04);
    transition:0.2s ease-out all;
}
`
